import{_ as e,c as o,o as l,e as i}from"./app.2fae1e82.js";const C='{"title":"TCP\u548CUDP","description":"","frontmatter":{"title":"TCP\u548CUDP","date":"2020-02-29T00:00:00.000Z","tags":["\u7F51\u7EDC"],"categories":["\u8BA1\u7B97\u673A\u57FA\u7840"]},"headers":[{"level":2,"title":"TCP","slug":"tcp"},{"level":3,"title":"TCP\u4E09\u6B21\u63E1\u624B\u8FC7\u7A0B","slug":"tcp\u4E09\u6B21\u63E1\u624B\u8FC7\u7A0B"},{"level":3,"title":"TCP\u7684\u56DB\u6B21\u63E1\u624B","slug":"tcp\u7684\u56DB\u6B21\u63E1\u624B"},{"level":3,"title":"TCP\u7684\u8868\u5934","slug":"tcp\u7684\u8868\u5934"},{"level":2,"title":"UDP","slug":"udp"},{"level":3,"title":"\u7279\u70B9","slug":"\u7279\u70B9"},{"level":3,"title":"TCP \u4E0E UDP \u7684\u533A\u522B","slug":"tcp-\u4E0E-udp-\u7684\u533A\u522B"}],"relativePath":"posts/base/TCP_UDP.md","lastUpdated":1582989281000}',c={},t=i('<h2 id="tcp" tabindex="-1">TCP <a class="header-anchor" href="#tcp" aria-hidden="true">#</a></h2><p>\u4F20\u8F93\u63A7\u5236\u534F\u8BAE\uFF08<code>TCP</code>\uFF09\u662F\u4E00\u79CD\u9762\u5411\u8FDE\u63A5\u7684\u3001\u53EF\u9760\u7684\u3001\u57FA\u4E8E\u5B57\u8282\u6D41\u7684\u4F20\u8F93\u5C42\u901A\u4FE1\u534F\u8BAE\uFF0C\u7531<code>IETF</code>\u7684<code>RFC 793</code>\u5B9A\u4E49\u3002\u5728\u7B80\u5316\u7684\u8BA1\u7B97\u673A\u7F51\u7EDC<code>OSI</code>\u6A21\u578B\u4E2D\u7684\u4F20\u8F93\u5C42\u3002</p><h3 id="tcp\u4E09\u6B21\u63E1\u624B\u8FC7\u7A0B" tabindex="-1">TCP\u4E09\u6B21\u63E1\u624B\u8FC7\u7A0B <a class="header-anchor" href="#tcp\u4E09\u6B21\u63E1\u624B\u8FC7\u7A0B" aria-hidden="true">#</a></h3><p><img src="https://raw.githubusercontent.com/Moking1997/NotePhoto/master/20200229225736.png" alt=""></p><ol><li>\u5BA2\u6237\u7AEF\u901A\u8FC7\u5411\u670D\u52A1\u5668\u7AEF\u53D1\u9001\u4E00\u4E2A<code>SYN</code>\u6765\u521B\u5EFA\u4E00\u4E2A\u4E3B\u52A8\u6253\u5F00\uFF0C\u4F5C\u4E3A\u4E09\u6B21\u63E1\u624B\u7684\u4E00\u90E8\u5206\u3002\u5BA2\u6237\u7AEF\u628A\u8FD9\u6BB5\u8FDE\u63A5\u7684\u5E8F\u53F7\u8BBE\u5B9A\u4E3A\u968F\u673A\u6570X\u3002</li><li>\u670D\u52A1\u5668\u7AEF\u5E94\u5F53\u4E3A\u4E00\u4E2A\u5408\u6CD5\u7684<code>SYN</code>\u56DE\u9001\u4E00\u4E2A<code>SYN/ACK</code>\u3002<code>ACK</code>\u7684\u786E\u8BA4\u7801\u5E94\u4E3AX+1\uFF0C<code>SYN/ACK</code>\u5305\u672C\u8EAB\u53C8\u6709\u4E00\u4E2A\u968F\u673A\u4EA7\u751F\u7684\u5E8F\u53F7Y\u3002</li><li>\u6700\u540E\uFF0C\u5BA2\u6237\u7AEF\u518D\u53D1\u9001\u4E00\u4E2A<code>ACK</code>\u3002\u6B64\u65F6\u5305\u7684\u5E8F\u53F7\u88AB\u8BBE\u5B9A\u4E3AX+1\uFF0C\u800C<code>ACK</code>\u7684\u786E\u8BA4\u7801\u5219\u4E3AY+1\u3002\u5F53\u670D\u52A1\u7AEF\u6536\u5230\u8FD9\u4E2A<code>ACK</code>\u7684\u65F6\u5019\uFF0C\u5C31\u5B8C\u6210\u4E86\u4E09\u6B21\u63E1\u624B\uFF0C\u5E76\u8FDB\u5165\u4E86\u8FDE\u63A5\u521B\u5EFA\u72B6\u6001\u3002</li></ol><p>3\u6B21\u63E1\u624B\u7684\u7279\u70B9</p><p>\u6CA1\u6709\u5E94\u7528\u5C42\u7684\u6570\u636E ,SYN\u8FD9\u4E2A\u6807\u5FD7\u4F4D\u53EA\u6709\u5728TCP\u5EFA\u7ACB\u8FDE\u63A5\u65F6\u624D\u4F1A\u88AB\u7F6E1 ,\u63E1\u624B\u5B8C\u6210\u540ESYN\u6807\u5FD7\u4F4D\u88AB\u7F6E0\u3002</p><h3 id="tcp\u7684\u56DB\u6B21\u63E1\u624B" tabindex="-1">TCP\u7684\u56DB\u6B21\u63E1\u624B <a class="header-anchor" href="#tcp\u7684\u56DB\u6B21\u63E1\u624B" aria-hidden="true">#</a></h3><p><img src="https://raw.githubusercontent.com/Moking1997/NotePhoto/master/20200229230940.png" alt=""></p><ol><li><p>\u5F53 \u5BA2\u6237\u7AEF\u5B8C\u6210\u6570\u636E\u4F20\u8F93\u540E,\u5C06\u63A7\u5236\u4F4DFIN\u7F6E1\uFF0C\u63D0\u51FA\u505C\u6B62TCP\u8FDE\u63A5\u7684\u8BF7\u6C42</p></li><li><p>\u670D\u52A1\u5668\u6536\u5230FIN\u540E\u5BF9\u5176\u4F5C\u51FA\u54CD\u5E94\uFF0C\u786E\u8BA4\u8FD9\u4E00\u65B9\u5411\u4E0A\u7684TCP\u8FDE\u63A5\u5C06\u5173\u95ED,\u5C06ACK\u7F6E1</p></li><li><p>\u7531\u670D\u52A1\u5668\u518D\u63D0\u51FA\u53CD\u65B9\u5411\u7684\u5173\u95ED\u8BF7\u6C42,\u5C06FIN\u7F6E1</p></li><li><p>\u5BA2\u6237\u7AEF\u5BF9\u670D\u52A1\u5668\u7684\u8BF7\u6C42\u8FDB\u884C\u786E\u8BA4\uFF0C\u5C06ACK\u7F6E1\uFF0C\u53CC\u65B9\u5411\u7684\u5173\u95ED\u7ED3\u675F</p></li></ol><h3 id="tcp\u7684\u8868\u5934" tabindex="-1">TCP\u7684\u8868\u5934 <a class="header-anchor" href="#tcp\u7684\u8868\u5934" aria-hidden="true">#</a></h3><p><img src="https://raw.githubusercontent.com/Moking1997/NotePhoto/master/20200229221716.png" alt=""></p><ul><li>\u5E8F\u5217\u53F7\uFF08seq\uFF0C32\u4F4D\u957F\uFF09 <ul><li>\u5982\u679C\u542B\u6709\u540C\u6B65\u5316\u65D7\u6807\uFF08SYN\uFF09\uFF0C\u5219\u6B64\u4E3A\u6700\u521D\u7684\u5E8F\u5217\u53F7,\u7B2C\u4E00\u4E2A\u6570\u636E\u6BD4\u7279\u7684\u5E8F\u5217\u7801\u4E3A\u672C\u5E8F\u5217\u53F7\u52A0\u4E00\u3002</li><li>\u5982\u679C\u6CA1\u6709\u540C\u6B65\u5316\u65D7\u6807\uFF08SYN\uFF09\uFF0C\u5219\u6B64\u4E3A\u7B2C\u4E00\u4E2A\u6570\u636E\u6BD4\u7279\u7684\u5E8F\u5217\u7801\u3002</li></ul></li><li>\u7A97\u53E3\uFF08WIN\uFF0C16\u4F4D\u957F\uFF09 <ul><li>\u8868\u793A\u4ECE\u786E\u8BA4\u53F7\u5F00\u59CB\uFF0C\u672C\u62A5\u6587\u7684\u53D1\u9001\u65B9\u53EF\u4EE5\u63A5\u6536\u7684\u5B57\u8282\u6570\uFF0C\u5373\u63A5\u6536\u7A97\u53E3\u5927\u5C0F\u3002\u7528\u4E8E\u6D41\u91CF\u63A7\u5236</li></ul></li><li>\u6807\u8BC6\u7B26 <ul><li>URG=1\uFF1A\u8BE5\u5B57\u6BB5\u4E3A\u4E00\u8868\u793A\u672C\u6570\u636E\u62A5\u7684\u6570\u636E\u90E8\u5206\u5305\u542B\u7D27\u6025\u4FE1\u606F\uFF0C\u662F\u4E00\u4E2A\u9AD8\u4F18\u5148\u7EA7\u6570\u636E\u62A5\u6587\uFF0C\u6B64\u65F6\u7D27\u6025\u6307\u9488\u6709\u6548\u3002\u7D27\u6025\u6570\u636E\u4E00\u5B9A\u4F4D\u4E8E\u5F53\u524D\u6570\u636E\u5305\u6570\u636E\u90E8\u5206\u7684\u6700\u524D\u9762\uFF0C\u7D27\u6025\u6307\u9488\u6807\u660E\u4E86\u7D27\u6025\u6570\u636E\u7684\u5C3E\u90E8\u3002</li><li>ACK=1\uFF1A\u8BE5\u5B57\u6BB5\u4E3A\u4E00\u8868\u793A\u786E\u8BA4\u53F7\u5B57\u6BB5\u6709\u6548\u3002\u6B64\u5916\uFF0CTCP \u8FD8\u89C4\u5B9A\u5728\u8FDE\u63A5\u5EFA\u7ACB\u540E\u4F20\u9001\u7684\u6240\u6709\u62A5\u6587\u6BB5\u90FD\u5FC5\u987B\u628A ACK \u7F6E\u4E3A\u4E00\u3002</li><li>PSH=1\uFF1A\u8BE5\u5B57\u6BB5\u4E3A\u4E00\u8868\u793A\u63A5\u6536\u7AEF\u5E94\u8BE5\u7ACB\u5373\u5C06\u6570\u636E push \u7ED9\u5E94\u7528\u5C42\uFF0C\u800C\u4E0D\u662F\u7B49\u5230\u7F13\u51B2\u533A\u6EE1\u540E\u518D\u63D0\u4EA4\u3002</li><li>RST=1\uFF1A\u8BE5\u5B57\u6BB5\u4E3A\u4E00\u8868\u793A\u5F53\u524D TCP \u8FDE\u63A5\u51FA\u73B0\u4E25\u91CD\u95EE\u9898\uFF0C\u53EF\u80FD\u9700\u8981\u91CD\u65B0\u5EFA\u7ACB TCP \u8FDE\u63A5\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E8E\u62D2\u7EDD\u975E\u6CD5\u7684\u62A5\u6587\u6BB5\u548C\u62D2\u7EDD\u8FDE\u63A5\u8BF7\u6C42\u3002</li><li>SYN=1\uFF1A\u5F53SYN=1\uFF0CACK=0\u65F6\uFF0C\u8868\u793A\u5F53\u524D\u62A5\u6587\u6BB5\u662F\u4E00\u4E2A\u8FDE\u63A5\u8BF7\u6C42\u62A5\u6587\u3002\u5F53SYN=1\uFF0CACK=1\u65F6\uFF0C\u8868\u793A\u5F53\u524D\u62A5\u6587\u6BB5\u662F\u4E00\u4E2A\u540C\u610F\u5EFA\u7ACB\u8FDE\u63A5\u7684\u5E94\u7B54\u62A5\u6587\u3002</li><li>FIN=1\uFF1A\u8BE5\u5B57\u6BB5\u4E3A\u4E00\u8868\u793A\u6B64\u62A5\u6587\u6BB5\u662F\u4E00\u4E2A\u91CA\u653E\u8FDE\u63A5\u7684\u8BF7\u6C42\u62A5\u6587\u3002</li></ul></li></ul><h2 id="udp" tabindex="-1">UDP <a class="header-anchor" href="#udp" aria-hidden="true">#</a></h2><p>\u7528\u6237\u6570\u636E\u62A5\u534F\u8BAE\uFF08<code>UDP</code>\uFF09\u662F\u540C\u4E00\u5C42\u5185\u53E6\u4E00\u4E2A\u91CD\u8981\u7684\u4F20\u8F93\u534F\u8BAE,\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u9762\u5411\u6570\u636E\u62A5\u7684\u901A\u4FE1\u534F\u8BAE,\u662F\u4E00\u79CD\u65E0\u8FDE\u63A5\u7684\u534F\u8BAE\u3002</p><p><strong><code>UDP</code>\u6709\u4E0D\u63D0\u4F9B\u6570\u636E\u5305\u5206\u7EC4\u3001\u7EC4\u88C5\u548C\u4E0D\u80FD\u5BF9\u6570\u636E\u5305\u8FDB\u884C\u6392\u5E8F\u7684\u7F3A\u70B9</strong>,\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5F53\u62A5\u6587\u53D1\u9001\u4E4B\u540E\uFF0C\u662F\u65E0\u6CD5\u5F97\u77E5\u5176\u662F\u5426\u5B89\u5168\u5B8C\u6574\u5230\u8FBE\u7684\u3002</p><h3 id="\u7279\u70B9" tabindex="-1">\u7279\u70B9 <a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a></h3><ol><li><p><strong><code>UDP</code>\u662F\u65E0\u8FDE\u63A5\u7684</strong>\uFF0C\u5373\u53D1\u9001\u6570\u636E\u4E4B\u524D\u4E0D\u9700\u8981\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u56E0\u6B64\u51CF\u5C11\u4E86\u5F00\u9500\u548C\u53D1\u9001\u6570\u636E\u4E4B\u524D\u7684\u65F6\u5EF6\u3002</p></li><li><p><strong><code>UDP</code>\u662F\u4E0D\u53EF\u9760\u7684</strong>,\u56E0\u4E3A<code>UDP</code>\u4F7F\u7528\u5C3D\u6700\u5927\u52AA\u529B\u4EA4\u4ED8\uFF0C\u5373\u4E0D\u4FDD\u8BC1\u53EF\u9760\u4EA4\u4ED8\uFF0C\u56E0\u6B64\u4E3B\u673A\u4E0D\u9700\u8981\u7EF4\u6301\u590D\u6742\u7684\u8FDE\u63A5\u72B6\u6001\u8868\u3002</p></li><li><p><strong><code>UDP</code>\u662F\u9762\u5411\u62A5\u6587\u7684</strong>\u3002\u53D1\u9001\u65B9\u7684<code>UDP</code>\u5BF9\u5E94\u7528\u7A0B\u5E8F\u4EA4\u4E0B\u6765\u7684\u62A5\u6587\uFF0C\u5728\u6DFB\u52A0\u9996\u90E8\u540E\u5C31\u5411\u4E0B\u4EA4\u4ED8IP\u5C42\u3002<code>UDP</code>\u5BF9\u5E94\u7528\u5C42\u4EA4\u4E0B\u6765\u7684\u62A5\u6587\uFF0C\u65E2\u4E0D\u5408\u5E76\uFF0C\u4E5F\u4E0D\u62C6\u5206\uFF0C\u800C\u662F\u4FDD\u7559\u8FD9\u4E9B\u62A5\u6587\u7684\u8FB9\u754C\u3002\u56E0\u6B64\uFF0C\u5E94\u7528\u7A0B\u5E8F\u5FC5\u987B\u9009\u62E9\u5408\u9002\u5927\u5C0F\u7684\u62A5\u6587\u3002</p></li><li><p><strong><code>UDP</code>\u6CA1\u6709\u62E5\u585E\u63A7\u5236</strong>,\u56E0\u6B64\u7F51\u7EDC\u51FA\u73B0\u7684\u62E5\u585E\u4E0D\u4F1A\u4F7F\u6E90\u4E3B\u673A\u7684\u53D1\u9001\u901F\u7387\u964D\u4F4E\u3002\u5F88\u591A\u7684\u5B9E\u65F6\u5E94\u7528\uFF08\u5982IP\u7535\u8BDD\u3001\u5B9E\u65F6\u89C6\u9891\u4F1A\u8BAE\u7B49\uFF09\u8981\u53BB\u6E90\u4E3B\u673A\u4EE5\u6052\u5B9A\u7684\u901F\u7387\u53D1\u9001\u6570\u636E\uFF0C\u5E76\u4E14\u5141\u8BB8\u5728\u7F51\u7EDC\u53D1\u751F\u62E5\u585E\u65F6\u4E22\u5931\u4E00\u4E9B\u6570\u636E\uFF0C\u4F46\u5374\u4E0D\u5141\u8BB8\u6570\u636E\u6709\u592A\u591A\u7684\u65F6\u5EF6\u3002<code>UDP</code>\u6B63\u597D\u7B26\u5408\u8FD9\u79CD\u8981\u6C42\u3002</p></li><li><p><code>UDP</code>\u652F\u6301\u4E00\u5BF9\u4E00\u3001\u4E00\u5BF9\u591A\u3001\u591A\u5BF9\u4E00\u548C\u591A\u5BF9\u591A\u7684\u4EA4\u4E92\u901A\u4FE1\u3002</p></li><li><p><code>UDP</code>\u7684\u9996\u90E8\u5F00\u9500\u5C0F\uFF0C\u53EA\u67098\u4E2A\u5B57\u8282\uFF0C\u6BD4TCP\u768420\u4E2A\u5B57\u8282\u7684\u9996\u90E8\u8981\u77ED\u3002 <img src="https://raw.githubusercontent.com/Moking1997/NotePhoto/master/20200229221347.png" alt=""></p></li></ol><ul><li><p>\u6E90\u7AEF\u53E3:\u5728\u9700\u8981\u5BF9\u65B9\u56DE\u4FE1\u65F6\u9009\u7528\u3002\u4E0D\u9700\u8981\u65F6\u53EF\u7528\u51680\u3002</p></li><li><p>\u76EE\u7684\u7AEF\u53E3:\u8FD9\u5728\u7EC8\u70B9\u4EA4\u4ED8\u62A5\u6587\u65F6\u5FC5\u987B\u8981\u4F7F\u7528\u5230\u3002</p></li><li><p>\u957F\u5EA6:UDP\u7528\u6237\u6570\u636E\u62A5\u7684\u957F\u5EA6\uFF0C\u5176\u6700\u5C0F\u503C\u662F8\uFF08\u4EC5\u6709\u9996\u90E8\uFF09</p></li><li><p>\u68C0\u9A8C\u548C:\u68C0\u6D4BUDP\u7528\u6237\u6570\u636E\u62A5\u5728\u4F20\u8F93\u4E2D\u662F\u5426\u6709\u9519\u3002\u6709\u9519\u5C31\u4E22\u5F03\u3002</p></li></ul><h3 id="tcp-\u4E0E-udp-\u7684\u533A\u522B" tabindex="-1"><code>TCP</code> \u4E0E <code>UDP</code> \u7684\u533A\u522B <a class="header-anchor" href="#tcp-\u4E0E-udp-\u7684\u533A\u522B" aria-hidden="true">#</a></h3><ol><li><p><code>TCP</code>\u57FA\u4E8E\u8FDE\u63A5,<code>UDP</code>\u662F\u65E0\u8FDE\u63A5</p></li><li><p>\u5BF9\u7CFB\u7EDF\u8D44\u6E90\u7684\u8981\u6C42\uFF08<code>TCP</code>\u8F83\u591A\uFF0C<code>UDP</code>\u5C11\uFF09</p></li><li><p><code>UDP</code>\u7A0B\u5E8F\u7ED3\u6784\u8F83\u7B80\u5355,\u901F\u5EA6\u5FEB</p></li><li><p>\u6D41\u6A21\u5F0F\u4E0E\u6570\u636E\u62A5\u6A21\u5F0F</p></li><li><p><code>TCP</code>\u4FDD\u8BC1\u6570\u636E\u6B63\u786E\u6027\uFF0C<code>UDP</code>\u53EF\u80FD\u4E22\u5305</p></li><li><p><code>TCP</code>\u4FDD\u8BC1\u6570\u636E\u987A\u5E8F\uFF0C<code>UDP</code>\u4E0D\u4FDD\u8BC1\u3002</p></li></ol>',21),d=[t];function p(a,r,s,n,P,h){return l(),o("div",null,d)}var g=e(c,[["render",p]]);export{C as __pageData,g as default};
