import{_ as t,c as o,o as e,e as a}from"./app.4a41aebd.js";const b='{"title":"\u5929\u9F99\u516B\u90E8","description":"","frontmatter":{"date":"2020-12-01T00:00:00.000Z","title":"\u5929\u9F99\u516B\u90E8","tags":["\u968F\u7B14","\u8BFB\u4E66\u611F"],"describe":"\u5929\u9F99\u516B\u90E8"},"relativePath":"posts/book/tlbb.md","lastUpdated":1633867988904}',p={},_=a('<h1 id="\u5929\u9F99\u516B\u90E8" tabindex="-1">\u5929\u9F99\u516B\u90E8 <a class="header-anchor" href="#\u5929\u9F99\u516B\u90E8" aria-hidden="true">#</a></h1><p>\u8BFB\u5B8C\u91D1\u5EB8\u5148\u751F\u7684<code>&lt;&lt;\u5929\u9F99\u516B\u90E8&gt;&gt;</code>,\u771F\u662F\u611F\u53F9\u5148\u751F\u7684\u6587\u5316\u5E95\u8574,\u4E66\u4E2D\u4F5B\u7ECF,\u8BD7\u7ECF\u7684\u5F15\u7528\u4FE1\u624B\u634F\u6765.</p><p>\u5C0F\u65F6\u5019\u770B\u7535\u89C6\u5267\u4E0D\u77E5\u4E66\u540D\u4F55\u610F,\u53EA\u89C9\u5F97\u8FD9\u4E66\u540D\u5C31\u5341\u5206\u5389\u5BB3,\u770B\u4E86\u91D1\u5EB8\u5148\u751F\u5199\u7684\u91CA\u540D,\u624D\u77E5\u6653\u8FD9\u4E66\u540D\u7684\u542B\u4E49.</p><blockquote><p>\u201C\u5929\u9F99\u516B\u90E8\u201D\u8FD9\u540D\u8BCD\u51FA\u4E8E\u4F5B\u7ECF\u3002\u5982\u300A\u6CD5\u534E\u7ECF\xB7\u63D0\u5A46\u8FBE\u591A\u54C1\u300B\uFF1A\u201C\u5929\u9F99\u516B\u90E8\u3001\u4EBA\u4E0E\u975E\u4EBA\uFF0C\u7686\u9065\u89C1\u5F7C\u9F99\u5973\u6210\u4F5B\u3002\u201D\u201C\u975E\u4EBA\u201D\u662F\u5F62\u8C8C\u4F3C\u4EBA\u800C\u5B9E\u9645\u4E0D\u662F\u4EBA\u7684\u4F17\u751F\u3002\u201C\u5929\u9F99\u516B\u90E8\u201D\u90FD\u662F\u201C\u975E\u4EBA\u201D\uFF0C\u5305\u62EC\u516B\u79CD\u795E\u9053\u602A\u7269\uFF0C\u56E0\u4E3A\u4EE5\u201C\u5929\u201D\u53CA\u201C\u9F99\u201D\u4E3A\u9996\uFF0C\u6240\u4EE5\u79F0\u4E3A\u201C\u5929\u9F99\u516B\u90E8\u201D\u3002\u516B\u90E8\u8005\uFF0C\u4E00\u5929\uFF0C\u4E8C\u9F99\uFF0C\u4E09\u591C\u53C9\uFF0C\u56DB\u4E7E\u8FBE\u5A46\uFF0C\u4E94\u963F\u4FEE\u7F57\uFF0C\u516D\u8FE6\u697C\u7F57\uFF0C\u4E03\u7D27\u90A3\u7F57\uFF0C\u516B\u6469\u547C\u7F57\u8FE6\u3002</p></blockquote><p>\u4E66\u4E2D\u4ECE\u4E3B\u89D2\u6BB5\u8A89\u800C\u8D77,\u7531\u6BB5\u8A89\u800C\u7EC8\u6B62.</p><p>\u4E66\u4E2D\u5927\u90E8\u5206\u4EBA\u7269\u90FD\u662F&quot;\u60F3\u800C\u4E0D\u53EF\u5F97&quot;, \u4E09\u4E2A\u4E3B\u89D2\u4E5F\u66F4\u662F\u5982\u6B64.</p><p>\u6BB5\u8A89\u4ECE\u65E0\u91CF\u5C71\u6D1E\u4E2D\u89C1\u8FC7\u795E\u4ED9\u59D0\u59D0\u540E,\u7531\u6B64\u79CD\u4E0B&quot;\u5FC3\u9B54&quot;,\u4E0E\u6728\u5A49\u6E05\u76F8\u9047\u5374\u5F97\u77E5\u5176\u4E3A\u81EA\u5DF1\u7684\u4EB2\u59CA\u59B9,\u800C\u81EA\u5DF1\u559C\u6B22\u7684\u4E00\u4E2A\u5C0F\u59B9\u5B50\u949F\u7075\u513F\u7ADF\u7136\u4E5F\u662F\u81EA\u5DF1\u7684\u59CA\u59B9.\u81EA\u5DF1\u7684\u7236\u4EB2\u5E74\u8F7B\u65F6\u98CE\u6D41\u6210\u6027,\u7559\u4E0B\u4E86\u591A\u5C11\u60C5\u503A,\u90FD\u8FD8\u5728\u4E86\u513F\u5B50\u8EAB\u4E0A.\u4E4B\u540E\u53C8\u9047\u5230\u4E86\u4E0E\u795E\u4ED9\u59D0\u59D0\u4E00\u6A21\u4E00\u6837\u7684\u738B\u8BED\u5AE3,\u7531\u6B64\u89E6\u53D1\u4E86&quot;\u5FC3\u9B54&quot;,\u4E3A\u738B\u8BED\u5AE3\u795E\u9B42\u98A0\u5012,\u4E0D\u53EF\u81EA\u62D4.\u5948\u5C14\u8BED\u5AE3\u5FC3\u4E2D\u53EA\u6709\u8868\u54E5\u6155\u5BB9\u590D\u4E00\u4EBA.\u7EB5\u4F7F\u5982\u6B64,\u6BB5\u8A89\u4ECD\u65E7\u539A\u7740\u8138\u76AE\u8DDF\u5728\u5176\u8EAB\u65C1,\u53EA\u60F3\u8FDC\u8FDC\u7684\u770B\u7740\u4E5F\u597D.\u6700\u7EC8\u5728\u6155\u5BB9\u590D\u591A\u6B21\u5BF9\u738B\u8BED\u5AE3\u65E0\u60C5\u4E4B\u540E,\u738B\u8BED\u5AE3\u624D\u53D1\u73B0\u4E86\u6BB5\u8A89\u7684\u597D,\u613F\u4E0E\u6BB5\u8A89\u53CC\u5BBF\u53CC\u98DE.\u7136\u800C,\u5929\u610F\u5F04\u4EBA,\u5728\u81EA\u5DF1\u9762\u524D\u81EA\u5DF1\u7684\u7236\u4EB2\u6BCD\u4EB2\u60E8\u6B7B,\u800C\u81EA\u5DF1\u7684\u751F\u7236\u7ADF\u7136\u662F&quot;\u5929\u4E0B\u7B2C\u4E00\u5927\u6076\u4EBA&quot;,\u738B\u8BED\u5AE3\u662F\u81EA\u5DF1\u7684\u517B\u7236\u6240\u751F.,\u6B64\u65F6&quot;\u5FC3\u9B54&quot;\u7834\u89E3,\u738B\u8BED\u5AE3\u5BF9\u81EA\u5DF1\u6765\u8BF4\u4E5F\u53EF\u6709\u53EF\u65E0\u4E86.\u771F\u662F\u201C\u60F3\u800C\u4E0D\u80FD\uFF0C\u8981\u800C\u6240\u4E0D\u5F97\u201D</p><p>\u4E54\u5CF0\u4EBA\u7269\u7684\u8C6A\u8FC8\u9738\u6C14\u5728\u4E66\u4E2D\u63CF\u5199\u7684\u975E\u5E38\u591A,\u6B64\u4EBA\u7684\u7684\u8C6A\u8FC8\u4E4B\u6C14\u7834\u4E66\u800C\u51FA\u4E00\u822C\u8BA9\u4EBA\u9707\u64BC.\u5728\u521D\u6B21\u51FA\u573A\u4FBF\u4E0E\u6BB5\u8A89\u8C6A\u996E,\u55DC\u9152\u5982\u547D,\u6BCF\u4E00\u5206\u9152\u529B\u80FD\u5316\u4E3A\u6BCF\u4E00\u5206\u529B\u6C14\u4E00\u822C.\u7136\u800C\u5927\u82F1\u96C4,\u5374\u4E00\u751F\u90FD\u662F\u60B2\u5267. \u5148\u662F\u77E5\u6653\u81EA\u5DF1\u7ADF\u7136\u662F\u5951\u4E39\u4EBA,\u800C\u540E\u88AB\u8BEC\u9677\u6740\u7236\u4EB2\u6740\u5E08\u5085.\u88AB\u5929\u4E0B\u4EBA\u6240\u553E\u5F03.\u8FD9\u822C\u4E5F\u5C31\u7B97\u4E86,\u53C8\u5728\u8FFD\u67E5\u771F\u76F8\u7684\u8FC7\u7A0B\u4E2D\u4EB2\u624B\u6740\u6B7B\u4E86\u81EA\u5DF1\u5FC3\u7231\u7684\u5973\u4EBA\u963F\u6731,\u6700\u540E\u5728\u5C11\u6797\u5BFA\u5F97\u77E5\u7ADF\u662F\u81EA\u5DF1\u7684\u751F\u7236\u6740\u6B7B\u4E86\u5BF9\u81EA\u5DF1\u6709\u517B\u80B2\u4E4B\u6069\u7684\u7236\u6BCD\u548C\u5E08\u5085,\u53C8\u5982\u4F55\u62A5\u4EC7\u5462.\u6700\u7EC8\u81EA\u6740\u5728\u81EA\u5DF1\u6545\u4E8B\u7684\u8D77\u70B9\u96C1\u95E8\u5173\u907F\u514D\u5927\u5B8B\u548C\u5951\u4E39\u5175\u620E\u76F8\u89C1.\u4E66\u4E2D\u6700\u5927\u5FE0\u5927\u4E49\u4E4B\u4EBA\u786E\u5F97\u5230\u4E86\u6700\u60B2\u60E8\u7684\u547D\u8FD0,\u4EE4\u4EBA\u5618\u550F.</p><p>\u865A\u7AF9\u53EF\u8C13\u4E09\u4E2A\u4E3B\u89D2\u4E4B\u4E2D\u8FD0\u6C14\u6700\u597D\u7684\u4E00\u4EBA,\u610F\u5916\u89E3\u5F00&quot;\u73B2\u73D1&quot;\u68CB\u5C40,\u5F97\u900D\u9065\u6D3E\u638C\u95E8\u65E0\u5D16\u5B50\u4E03\u5341\u5E74\u529F\u529B,\u5FC3\u5584\u6551\u5F97\u5929\u5C71\u7AE5\u59E5\u800C\u9047\u5230\u5FC3\u7231\u4E4B\u4EBA&quot;\u68A6\u59D1&quot;,\u963B\u6B62\u5E08\u53D4\u5E08\u4F2F\u62FC\u547D,\u53C8\u5F97\u4E24\u4EBA\u529F\u529B.\u672C\u662F\u4E3A\u4E86\u52A9\u6BB5\u8A89\u5F97\u9A78\u9A6C\u524D\u5F80\u897F\u590F,\u800C\u53C8\u5BFB\u5F97&quot;\u68A6\u59D1&quot;\u674E\u6E05\u9732.\u900D\u9065\u6D3E\u638C\u95E8,\u7075\u9E6B\u5BAB\u5BAB\u4E3B,\u897F\u590F\u56FD\u9A78\u9A6C,\u6700\u540E\u8FD8\u6210\u4E86\u4E10\u5E2E\u5E2E\u4E3B.\u53EF\u8C13\u5E78\u8FD0\u81F3\u6781.\u867D\u4E5F\u6709\u4E0D\u53EF\u5F97,\u4E00\u53EF\u4E0D\u5F97\u662F\u81EA\u5DF1\u7684\u4EB2\u751F\u7236\u6BCD\u672C\u4ECE\u5C0F\u4FBF\u4E3A\u89C1\u8FC7,\u7136\u800C\u89C1\u9762\u7247\u523B\u4FBF\u53CC\u53CC\u81EA\u6740\u6B7B\u5728\u81EA\u5DF1\u9762\u524D,\u800C\u4E0D\u53EF\u5F97,\u81EA\u5DF1\u672C\u4E00\u5FC3\u53EA\u60F3\u505A\u4E00\u4E2A\u5C0F\u548C\u5C1A,\u5948\u4F55\u4E0A\u5929\u4E0D\u8BB8.\u91D1\u5EB8\u5E94\u8BE5\u8FD8\u662F\u5BF9\u8FD9\u4E2A\u4E3B\u89D2\u624B\u4E0B\u7559\u60C5\u4E86.</p><p>\u4E66\u4E2D\u4E09\u4E2A\u4E3B\u89D2\u7684\u5173\u7CFB\u5176\u5B9E\u5E76\u4E0D\u7D27\u5BC6,\u4E00\u8D77\u51FA\u573A\u7684\u6B21\u6570\u4E5F\u4E0D\u591A,\u4E0D\u8FC7\u5728\u4E3B\u89D2\u4E0E\u4E3B\u89D2\u4E4B\u95F4\u7684\u6545\u4E8B\u94FE\u63A5\u9887\u4E3A\u5DE7\u5999,\u7531\u6BB5\u8A89\u5F15\u51FA\u4E54\u5CF0,\u6709\u4E54\u5CF0\u5F15\u56DE\u6BB5\u8A89,\u53C8\u6709\u6BB5\u8A89\u5F15\u51FA\u865A\u7AF9,\u6700\u540E\u4E09\u4E2A\u540C\u6846\u51FA\u573A,\u6700\u540E\u53C8\u7531\u6BB5\u8A89\u7ED3\u675F\u6545\u4E8B.\u4E66\u4E2D\u5176\u4ED6\u7684\u4EBA\u7269\u5F62\u8C61\u4E5F\u5F88\u4E30\u6EE1,\u6155\u5BB9\u590D,\u4E01\u6625\u79CB,\u6E38\u5766\u4E4B,\u963F\u7D2B....\u6BCF\u4E2A\u4EBA\u7269\u90FD\u8BA9\u4EBA\u5370\u8C61\u6DF1\u523B,\u4E5F\u5404\u81EA\u6709\u5176\u7279\u70B9.\u6BCF\u4E2A\u4EBA\u4E5F\u7686\u662F\u201C\u60F3\u800C\u4E0D\u53EF\u5F97&quot;,\u7EB5\u4F7F\u6B66\u529F\u518D\u9AD8,\u60F3\u5F97\u4E4B\u4E8B\u53C8\u8C08\u4F55\u5BB9\u6613,\u7EC8\u6709\u4E0D\u5982\u6240\u613F\u4E4B\u4E8B,\u4E0D\u5982\u505A\u4E00\u4E2A\u5C11\u6797\u626B\u5730\u50E7,\u5FC3\u7A7A\u660E\u9759.</p>',10),s=[_];function c(u,r,d,n,q,i){return e(),o("div",null,s)}var h=t(p,[["render",c]]);export{b as __pageData,h as default};
